SET NAMES UTF8;
DROP DATABASE IF EXISTS mice;
CREATE DATABASE mice CHARSET=UTF8;
USE mice;
#1.展会简介
CREATE TABLE exhibition(
	ex_id INT PRIMARY KEY AUTO_INCREMENT,
	ex_name VARCHAR(64),
	ex_start_time VARCHAR(32),
	ex_end_time VARCHAR(32),
	ex_country VARCHAR(64),
	ex_area VARCHAR(64),
	ex_city VARCHAR(64),
	ex_industry VARCHAR(64),#行业
	ex_hall VARCHAR(64),
	ex_organizer VARCHAR(64),
	ex_undertake VARCHAR(64),
	ex_intr VARCHAR(1024),#展会介绍
	ex_range VARCHAR(1024),#展示范围
	ex_promotion VARCHAR(1024),#展会宣传
	ex_why VARCHAR(1024),#为何参展
	ex_highlight VARCHAR(1024),#展会亮点
	ex_addtime VARCHAR(32),#添加日期
	ex_readtime INT,
	ex_address VARCHAR(64),#地址
	ex_phone VARCHAR(16),
	ex_people VARCHAR(16),
	ex_email VARCHAR(32),
	ex_url VARCHAR(64),#网址
	ex_preintr VARCHAR(1024)
);
	
INSERT INTO exhibition VALUES
(NULL,'2019东莞国际表面处理及涂装展览会
','2019-11-14','2019-11-17','中国','广东','东莞','化工/能源/环保','广东现代国际展览中心','上海励莱展览服务有限公司',NULL,'欢迎参加“2019东莞国际表面处理及涂装展览会”，此会是华南表面处理及涂装行业顶级品牌展览之一。展会将于2019年11月14日-17日在广东国际现代展览中心隆重开幕，全球工业制造领域一线厂商将悉数到场。展会以机械及表面处理及涂装应用为最大亮点，倾情为展商及观众呈现一个高品质的行业盛会。','表面处理、精饰设备及工程：喷涂、电子抛光、电泳涂装、阳极氧化、着色转化涂装、连续条纹电镀、抗化学品容器及衬里、电镀挂架设计、过滤及钝化、泵、搅拌及混合、浸渍加热炉设计、含氟聚合物热交换器等。机械式及化学式处理：研磨/抛光/打蜡、喷砂或喷丸设备、去毛刺、防锈、清洗、脱脂、化学除锈、磷化、电清洗、去离子、蒸汽脱脂、超声波、浸渍、酸洗等。涂装设备及生产线：油漆涂装、粉末涂装、汽车涂装线、家用电器涂装线、机械手涂装、彩涂板与热镀锌、空气及无气喷涂、磨擦静电喷涂、 电晕喷涂、往复喷涂机、喷涂室、固化炉、运输带、工程服务等。电镀工艺及镀液：电镀、电刷镀、化学镀、金属及合金电镀、真空电镀、非导电体金属化、塑胶电镀、电子成型、浸渍电镀、滚桶电镀、电镀助剂、中间体、镀铬、镀铜、镀金、镀铂、镀银、镀锡、 铜及锡一铜、镀锡一镍、镀锌、镀锌合金。','　A.通过200家以上的专业合作媒体发布展会广告和文字宣传，不间断地宣传本届展会，时间长达10个月，预计受众将达到10万人次。B.将通过超过100家的大型网络媒体发布本届展会的预告信息和展会的软性新闻稿，篇数超过500篇。C.本届展会计划在其它相关展会派发、邮寄等方式发出邀请函、入场券等展会相关邀请信息10万张，群发短信100万条，针对专业买家，实行预约登记制。D.针对政府部门、行业协会和专业机构的高层领导，展会办公室通过电话联系、上门拜访、峰会推广等方式，广泛邀请业界高层人士到会。','A.现场达成交易：展览的时间虽然短，但由于直接与商家进行面对面的会谈，便于达成协议或意向。B.企业及产品宣传：展览会是一种立体广告，增进采购商对产品、服务的了解，宜于接受。C.树立企业形象：在同行业和用户领域树立好的企业形象、提升行业地位。D.加深市场了解：在与采购商交流中了解市场的需求，比日常的市场调研要直观和准确。',NULL,'2019-7-18','1301','上海市奉贤区岚丰路1150号6幢3726室','16621383379','刘文超','shll-wxlwc168@foxmail.com',NULL,NULL);
INSERT INTO exhibition VALUES(NULL,'2019东莞国际内部物流解决方案及流程管理展览会','2019-11-14','2019-11-17','中国','广东','东莞','运输/物流/仓储','广东现代国际展览中心','上海励莱展览服务有限公司',NULL,'SIM2019东莞国际内部物流解决方案及流程管理展览会携手SIA2019东莞智能工厂展于2019年11月14-17日在广东现代国际展览中心举办。两大展会，共享预计100,000平方米的展会规模，近距离观摩与领略全球先进的物流产品与技术、寻求全方位最优生产、仓储、配送、供应链物流方案。展会现场全称贯穿高品质论坛，研讨主题聚焦电商、制药、机械、智能制造、汽车、新能源、食品冷链、机器人、自动化、零售等应用行业需求领域热点，助力企业优化供应链、提高物流效率、节约物流成本。','(1)仓库和生产控制系统/机器人：控制系统和设备、工业机器人、制造执行系统(MES)、精益生产规划、人力资源规划、ERP和PPS系统、规划和研发、科研和技术、工业机器人，移动机器人包装、称重、测量和邮资系统:一次性和多次性包装、工业包装、衬垫保护和棱边保护、包装材料、托盘、包装及拆解设备、托盘分拣、托盘吊装机器人、收缩机拉伸设备、捆绑技术、称量设备、测量系统及设备、邮资系统、包装技术开发及咨询等；(2)仓储设备和操作设备：货架及货架设备、货架操作设备、货架维修和检查、货架保护隔栏、拣选设备、看板系统及解决方案、仓库和帐篷建造、隔间墙及仓储办公室设备、仓库地面及仓库照明、仓库和仓库设施的清洗设备、空调系统、货梯系统、工业门及密封装置、工作垫、工位系统、仓库和地面标记、地面保护及集尘器垫、危险品处理和储存等；(3)信息通讯技术:供应商关系管理系统(SRM)、客户关系管理系统(CRM)、信息整合、货物追踪和识别系统、感应系统及方案、电信、人机交互系统、云端服务及解决方案、供应链软件、视频追踪及监控等；(4)货物装卸技术和系统:装卸坡道、装卸桥、液压平台、起重台、可调节坡道、转运设备、货车自动装载和卸装装置、装卸区安全技术、拉紧带和拉紧链、防滑垫、防滑托盘、防撞产品等；','强大的宣传攻势——通过地铁、电视、网络、平面、电台、户外广告、商务办公楼电梯广告等7大宣传渠道。中央电视台、上海电视台及100多家行业媒体、报刊、杂志等广泛宣传展会，形成由东莞辐射全国的媒体宣传网络。官方网站宣传——在宣传展会的基础，我们将努力推广大会官方网站，丰富网站信息内容，及时提供行业资讯、展商动态、参与展商网络链接。同时在百度优化搜索引擎及关键词设置，便于参观者搜寻“上海内部物流展”展会相关信息，并在官网预登记参观信息。微信宣传推广——及时更新微信公众号信息，发布最新参展商展品及公司简介，丰富公众号信息内容，及时提供行业资讯、展商动态、参与展商新品推荐，组织微信参观预登记营销活动。',NULL,NULL,'2019-7-18','1300','上海市奉贤区岚丰路1150号6幢3726室','16621383379','刘文超','shll-wxlwc168@foxmail.com',NULL,NULL);
INSERT INTO exhibition VALUES(NULL,'2019东莞国际智能仓储设备及应用技术展览会','2019-11-14','2019-11-17','中国','广东','东莞','运输/物流/仓储','广东现代国际展览中心','上海励莱展览服务有限公司',NULL,'由中国设备管理协会主办，上海励莱展览服务有限公司承办，2019东莞国际智能仓储设备及应用技术展览会定于2019年11月14至17日在广东现代国际展览中心举行。作为SIA东莞智能工厂展览会旗下的一个分支展，展会重点展示包括智能叉车、物流系统集成、物流机器人、agv、绿色搬运技术、锂电快充技术、激光slam无人导引技术、智能工厂方案等在内的高新产品及技术。','1、智能立体仓库案例、无人仓储技术解决方案、各类 agv 小车及配件等；2、智能存储系统、智能输送系统、自动码垛系统、智能分拣系统、智能仓储系统集成商、总承包商等；3、物料搬运及仓储系统：储存及搬运系统、自动化及控制系统吊车及起重机、连续搬运设备、升降机、搬运设备输送设备、 行李搬运设备等；4、自动识别系统、rfid电子标签、读写器、无线传感器网络技术、视频监控系统等；5、仓库管理系统（wms）、仓储控制系统（wcs）、智能工厂解决方案、仓储云等；',NULL,NULL,'1、市场需求巨大：智能搬运行业前景良好，应用领域广泛，是国民经济发展不可缺少的行业，近年来国家的战略方针也为机器人行业带来更大的发展机遇。2、SIA经过多年的耕耘，已经成为行业内不可错缺的行业盛会，并赢得行业多家知名企业的信赖与认可，展会将集中展示行业新产品、技术、装备与解决方案等。3、多场专业活动，促进行业交流：将邀请行业协会、学会、专家学者、社会资本家、国外行业专家学者莅临现场一起探讨行业发展方向，分享先进技术；供需配对会更是深受欢迎真正为双方提供零距离的商贸平台。4、全球推广网络，多家媒体共造势：国外多家媒体全方面报道宣传、国内专业媒体同步宣传推广，结合电子邮件、电话、印刷品邮寄等方式向企业进行一对一的邀请。','2019-7-18','1306','上海市奉贤区岚丰路1150号6幢3726室','16621388379','刘文超','shll-wxlwc168@foxmail.com',NULL,NULL);
INSERT INTO exhibition VALUES(NULL,'2019东莞国际伺服、运动控制及直线运动产业展览会','2019-11-14','2019-11-17','中国','广东','东莞','机械/材料/设备展','广东现代国际展览中心','上海励莱展览服务有限公司',NULL,'为了全面推进实施制造强国战略，大力提高直线运动在相关行业的发展效率，为此在各级政府部门、行业协会的关心与支持下、经过组织单位的精心组织策划，上海励莱展览服务有限公司联合行业权威机构定于2019年11月14日在广东现代国际展览中心举办“2019东莞国际伺服、运动控制及直线运动产业展览会”展会重点交流国内外伺服、直线运动行业现状与发展方向。通过大规模、全方位、多层次、统一性的国际伺服、直线运动产业“会”“展”，打造我国伺服、直线运动行业首个集产业博览、贸易洽谈、高峰论坛于一体的综合性会展平台。','1、直线模组、半封闭丝杆直线模组、半封闭同步带直线模组、全封闭丝杆直线模组、全封闭同步带直线模组等；2、直角坐标机器人、桁架机器人、水平多关节机器人、六轴工业机器人、全自动点胶机、高速拾放机器人、六自由度电动平台、 龙门式机器人、悬臂式机器人、单轴机器人等；3、直线运动滑台、工业铝型材、直线导轨、直线提升运动单元、升降机、直线运动轴承、导向轴、导向轴支座、直线执行器、电动推杆、电液推杆、电动缸、直线运动单元、滚珠丝杆、皮带传动、齿条传动等；4、直线电机、直线潜油电机、管形直线电机、直线振荡电机、平板直线电机、直线感应电机等。',NULL,NULL,'在全球智能制造大行其道的今天,工业自动化市场成为全世界各国经济持续发展的重要根据地。,预计到2020年,全球工业自动化市场的规模将达到2000多亿美元。随着中国制造业升级的递进，中国伺服、运动控制市场逐渐成熟，在机床、雕刻机、半导体、工业机器人、EMS、物料搬运等多数机械行业均取得良好的发展。借力“十三五”规划、中国制造2025计划，依托机器人及自动化产业和全球最大的潜在市场资源，直线运动作为机器人及自动化产业的关键零部件，在机器人及自动化行业中起到积极推动作用。','2019-7-18','1307','上海市奉贤区岚丰路1150号6幢3726室','16621383379','刘文超','shll-wxlwc168@foxmail.com',NULL,NULL );
INSERT INTO exhibition VALUES(NULL,'2019上海国际芳香及香薰精油养生产业展览会','2019-11-30','2019-12-2','中国','上海','上海','医疗/保健/生物','上海光大会展中心','上海仪诺展览服务有限公司','玖新文化传媒（上海）有限公司 上海仪诺展览服务有限公司','芳香植物在欧洲、拉丁美洲及亚洲其他国家，已经被广泛应用于医药、美容、养生行业、食品加工业和其他消费品生产中。芳香植物的主要提取物是精油。北美、西欧及日本是精油的主要市场，其精油消费量约占全球市场的80%。同时，国外在大量发展芳香制造业的同时，也大力打造芳香产品的品牌效应。芳香产品除了具备休闲养生功能外，还具有理疗性功能。据预测，到2020年，全球芳香产业的产值将以平均大于4%的增速发展，总产值有望超过200亿美元。','1.芳香精油及香薰：单方精油、复方精油、植物油、中药油、调和油、净油、花精、精油饰品、香薰产品2.香精香料及应用 ：天然食品、饮料香精香料、制药香精香料、日化香精香料、饲料天然香料添加3.纯植物洗护：纯植物洗护产品、精油皂及工具、天然香水4.芳香疗法：培训学校、研究机构、芳香母婴、芳疗工作室、宠物芳香、芳香照护5.香道文化：香艺表演、天然香材及香品、香饰品、古法和香产品、线香、盘香、塔香、香器具、创意香文化产品6.芳香产品设备：烘干设备、灭菌设备、蒸馏设备、储存设备、包装设备及材料、提取设备、检测及分装设备',NULL,NULL,NULL,'2019-7-18','1751','中国·上海光大会展中心','17521000388','卓伟 17521000388（同微信） QQ:3087083730@qq.com','3087083730@qq.com','www.hleexpo.com',NULL);
INSERT INTO exhibition VALUES(NULL,'2020日本咖啡食品饮料披萨展','2020-7-1','2020-7-3','日本','境外','横滨','食品/饮料/酒','横滨国际平和会议场','TSO 国际会展有限公司（TSO Internatinal Inc.）',NULL,'根据全日本咖啡协会(All Japan Coffee Association)的数据，日本目前在进口国的总消费量中排名第三。随着喝咖啡文化在日本的确立，日本有700多万家咖啡馆风格的餐厅。这种风格的餐厅已经在日本的餐饮行业取得了压倒性的存在。近年来，日本咖啡行业出现了一些扩张的诱因。首先，在便利店和超市里，新培育的咖啡摊的数量呈爆炸式增长，这使得喝咖啡的人口连续增加。第二，受全球第三波咖啡的影响，日本咖啡风格餐厅的数量不断增加，咖啡风格的餐厅主要是煮咖啡和店内/外的氛围，这也推动了日本咖啡文化的进一步发展。最后，随着日本人口动态步入超高龄社会，有足够时间和花费的退休一代开始光顾咖啡馆式餐厅，这成为他们新的生活方式，他们不再呆在家里，而是呆在咖啡馆式餐厅','A、咖啡及咖啡制品：咖啡豆、咖啡、咖啡伴侣、咖啡食品、咖啡饮料等；B、咖啡连锁加盟店；C、咖啡器具及设备：咖啡机、咖啡自动售饮机、咖啡烘烤研磨设备、各种咖啡磨豆机、各种咖啡、各种咖啡器皿等其他咖啡冲泡用具；D、咖啡加工生产设备及技术：咖啡豆处理设备、咖啡豆脱壳设备、咖啡筛选机、咖啡精喷洒烘干设备等；E、其它相关配套设施：咖啡馆装修设计单位、咖啡店经营管理软件、智能咖啡店运作系统等；',NULL,NULL,'1、日本100多家媒体支持与参与，宣传密度覆盖面广，密度大；2、多家行业协会倾力支持，行业龙头企业协办支持的行业盛会；3、充分利用10年多积累的50万条的专业观众数据库，组织国内外的专业人士参观！','2019-7-16','1747','上海市闵行区莲花南路1951号格兰大厦403室','18616981016 021-61200958','谢艳丽','1054348312@qq.com',NULL,NULL);
INSERT INTO exhibition VALUES(NULL,'2020年成都国际汽车零部件及售后服务展览会','2020-5-19','2020-5-21','中国','四川','成都','汽车/摩托车/配件','成都世纪城新国际展览中心','法兰克福展览',NULL,'由中国贸促会汽车行业委员会、法兰克福展览(上海)有限公司等单位主办的2019成都法兰克福汽配展CAPAS于5月24日至26日在成都世纪城新国际会展中心举办。展会期间迎来全球超1000家企业参展，20000名专业观众参会。作为四川省重点培育的十大品牌展会之一，成都汽配展立足于西南市场，在快速增长的市场环境下已成为中国西南地区领军的一站式商贸、交流与投资平台。本届展会聚焦新能源汽车产业，共设4个馆，计划展览面积45000平米。展会将全面升级同期活动内容与质量，从技术及门店经营、市场及趋势、产品及方案等多个角度，深度挖掘潜力巨大的西南市场。展会期间举办中国新能源汽车国际合作大会、智变·聚势·赢未来—汽车后市场创新发展峰会、中国汽车后市场产业联动赋能终端高峰论坛、汽车后市场环境保护与治理高峰论坛等丰富多样的同期活动，为现场展商及观众提供机会，深入了解细分市场、交流探讨业务、拓展知识技能。',NULL,NULL,NULL,NULL,'2019-7-16','2491','成都世纪城新国际会展中心','13917777094','苏经理','2409829839@qq.com',NULL,NULL);
